package day1;

public class 변수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age;
		age = 49;
		float weight = 70.44f;
		
		System.out.println(age);
		System.out.println(weight);
		
		age = 50;
		weight = weight+3;
		System.out.println(age);
		System.out.println(weight);                  //float(새로운 변수) //변수 이름은 같을 수 없다 변수=값으 ㄹ저장할 수 있는 메모리 공간에 붙이는 이름  //타입:int , float
	}

}
