package day2;

public class 반복문_for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=5; i++) {
			System.out.println("안녕");
			//for(변수 , 조건 ,증감값) i=i+1의 약자는 i++
			//1~10까지 홀수출력 (int i=1; i<=10; i=i+2)
			//1~10까지 짝수출력 (int i=2; i<=10; i=i+2)
			
				
			}
		}
	}